import React from 'react'
import AlbumInfo from '../components/AlbumInfo'

export class AlbumInfoContainer extends React.Component {

  constructor(props) {
     super(props);
     this.state = {
       album: []
     };
   }

  componentDidMount(){
    fetch("/api/v1/artists/1/albums/1")
    .then(resp => resp.json())
    .then(a => {
      this.setState({
        album: a
      })
    })
    .catch(error => console.log(error))
  }

  render() {
    let album = this.state.album;
    return (
      <div>
          <ul>
              {Object.keys(album).map((a, index) => <li key={index}>{a} : {album[a]}</li>)}
          </ul>
      </div>
  )
}

}
export default AlbumInfoContainer;
